metadataType.sparql:
WHERE {{ SELECT ?score as ?metadata
		WHERE {
			GRAPH <@graph> {
		?ppi rdf:subject | rdf:object <@fromUri> .
		?ppi rdf:object | rdf:subject <@toUri> .
		?instance rdf:type ?ppi .
		?instance @metadataRelationUri ?score .
		}}
		ORDER BY DESC(?score)
		LIMIT 1
		}}

WHERE {{ SELECT COUNT(?experiment) as ?metadata
		WHERE {
			GRAPH <@graph> {
		?ppi rdf:subject | rdf:object <@fromUri> .
		?ppi rdf:object | rdf:subject <@toUri> .
		?instance rdf:type ?ppi .
		?instance skos:relatedMatch ?experiment .
		}}}}

WHERE { GRAPH <@graph> {
				?ppi rdf:subject | rdf:object <@fromUri> .
				?ppi rdf:object | rdf:subject <@toUri> .
	?instance rdf:type ?ppi .
	?instance rdfs:isDefinedBy ?metadata .
	}}

